mapper.select:
select a.id,a.name, u.name as 'owner', a.startDate, a.endDate from tbl_activity a join tbl_user u on a.owner = u.id
and a.name like '%' #{name} '%'
and u.name like '%' #{owner} '%'
and a.startDate > #{startDate}
and a.endDate < #{endDate};
order by a.createTime desc

select count(*) from tbl_activity a join tbl_user u
on a.owner=u.id


        and a.name like '%' #{name} '%'


        and u.name like '%' #{owner} '%'


        and a.startDate > #{startDate}


        and a.endDate < #{endDate};

mapper.delete:
delete from tbl_activity
where id in

    #{id}

mapper.select:
select id, name, owner, startDate, endDate, cost, description
from tbl_activity
where id = #{id}
